// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./LPToken.sol";

abstract contract BasePool is LPToken {
    // Constants
    uint256 public constant MIN_LIQUIDITY = 1000; // 最小流动性要求
    uint256 public constant MAX_PRICE_IMPACT = 10; // 最大价格影响（10%）
    
    // State variables
    address private immutable _token0;
    address private immutable _token1;
    uint256 private _reserve0;
    uint256 private _reserve1;
    bool private _initialized;

    // Events
    event PoolInitialized(
        address indexed token0,
        address indexed token1,
        uint256 amount0,
        uint256 amount1
    );
    event Swap(
        address indexed sender,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut
    );
    event LiquidityAdded(
        address indexed provider,
        uint256 amount0,
        uint256 amount1,
        uint256 liquidity
    );
    event LiquidityRemoved(
        address indexed provider,
        uint256 amount0,
        uint256 amount1,
        uint256 liquidity
    );

    constructor(address token0_, address token1_) LPToken() {
        require(token0_ != address(0), "BasePool: ZERO_ADDRESS");
        require(token1_ != address(0), "BasePool: ZERO_ADDRESS");
        require(token0_ != token1_, "BasePool: IDENTICAL_ADDRESSES");

        _token0 = token0_;
        _token1 = token1_;
    }

    // Modifiers
    modifier whenInitialized() virtual override {
        require(_initialized, "BasePool: NOT_INITIALIZED");
        _;
    }

    modifier checkMinLiquidity() virtual {
        require(
            IERC20(_token0).balanceOf(address(this)) >= MIN_LIQUIDITY &&
            IERC20(_token1).balanceOf(address(this)) >= MIN_LIQUIDITY,
            "BasePool: INSUFFICIENT_LIQUIDITY"
        );
        _;
    }

    // View functions
    function getReserves() public view virtual returns (uint256 reserve0, uint256 reserve1) {
        return (_reserve0, _reserve1);
    }

    function token0() public view returns (address) {
        return _token0;
    }

    function token1() public view returns (address) {
        return _token1;
    }

    function isInitialized() public view returns (bool) {
        return _initialized;
    }

    // Internal functions
    function _updateReserves() internal {
        _reserve0 = IERC20(_token0).balanceOf(address(this));
        _reserve1 = IERC20(_token1).balanceOf(address(this));
    }

    function _checkPriceImpact(uint256 oldPrice, uint256 newPrice) internal pure {
        uint256 priceChange;
        if (newPrice > oldPrice) {
            priceChange = ((newPrice - oldPrice) * 100) / oldPrice;
        } else {
            priceChange = ((oldPrice - newPrice) * 100) / oldPrice;
        }
        require(priceChange <= MAX_PRICE_IMPACT, "BasePool: PRICE_IMPACT_TOO_HIGH");
    }

    // Abstract functions to be implemented by derived contracts
    function initialize(uint256 amount0) external virtual;
    function addLiquidity(uint256 amount0) external virtual returns (uint256 liquidity);
    function removeLiquidity(uint256 liquidity) external virtual returns (uint256 amount0, uint256 amount1);
    function swap(address tokenIn, uint256 amountIn, address tokenOut) external virtual returns (uint256 amountOut);
    function getRequiredAmount1(uint256 amount0) public view virtual returns (uint256);
}

contracts/LPToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract LPToken is ERC20, Pausable, Ownable {
    // 错误定义
    error CallerNotPool();
    error NotInitialized();
    error AlreadyInitialized();
    error ZeroAddress();
    error TransferAmountTooLow();
    
    // 常量定义
    uint256 public constant MAX_SUPPLY = type(uint256).max;
    uint256 public constant MINIMUM_TRANSFER_AMOUNT = 1;
    
    // 状态变量
    bool private _initialized;
    address private _pool;
    
    // 事件定义
    event PoolSet(address indexed pool);
    event TokenMinted(address indexed to, uint256 amount);
    event TokenBurned(address indexed from, uint256 amount);
    event TransferRestricted(address indexed from, address indexed to, uint256 amount);
    
    constructor() ERC20("LP Token", "LP") Ownable(msg.sender) {
        _initialized = false;
    }

    /**
     * @dev 获取最小转账金额
     */
    function minTransferAmount() external pure returns (uint256) {
        return MINIMUM_TRANSFER_AMOUNT;
    }

    /**
     * @dev 检查池是否已初始化
     */
    modifier whenInitialized() virtual {
        if (!_initialized) revert NotInitialized();
        _;
    }

    /**
     * @dev 只允许池合约调用
     */
    modifier onlyPool() {
        if (msg.sender != _pool) revert CallerNotPool();
        _;
    }

    /**
     * @dev 初始化LP代币，设置池合约地址
     * @param pool_ 池合约地址
     */
    function initialize(address pool_) external onlyOwner {
        if (_initialized) revert AlreadyInitialized();
        if (pool_ == address(0)) revert ZeroAddress();
        
        _pool = pool_;
        _initialized = true;
        emit PoolSet(pool_);
    }

    /**
     * @dev 铸造LP代币
     * @param to 接收者地址
     * @param amount 铸造数量
     */
    function mint(address to, uint256 amount) external whenInitialized onlyPool {
        require(to != address(0), "Invalid recipient");
        require(amount > 0, "Zero amount");
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");
        
        _mint(to, amount);
        emit TokenMinted(to, amount);
    }

    /**
     * @dev 销毁LP代币
     * @param from 持有者地址
     * @param amount 销毁数量
     */
    function burn(address from, uint256 amount) external whenInitialized onlyPool {
        require(from != address(0), "Invalid sender");
        require(amount > 0, "Zero amount");
        require(balanceOf(from) >= amount, "Insufficient balance");
        
        _burn(from, amount);
        emit TokenBurned(from, amount);
    }

    /**
     * @dev 暂停合约
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev 恢复合约
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 获取池合约地址
     */
    function pool() external view returns (address) {
        return _pool;
    }

    /**
     * @dev 获取初始化状态
     */
    function initialized() external view returns (bool) {
        return _initialized;
    }

    /**
     * @dev 重写转账函数，添加最小转账金额检查
     */
    function transfer(address to, uint256 amount) public override whenNotPaused returns (bool) {
        if (amount < MINIMUM_TRANSFER_AMOUNT) {
            emit TransferRestricted(msg.sender, to, amount);
            revert TransferAmountTooLow();
        }
        return super.transfer(to, amount);
    }

    /**
     * @dev 重写授权转账函数，添加最小转账金额检查
     */
    function transferFrom(address from, address to, uint256 amount) public override whenNotPaused returns (bool) {
        if (amount < MINIMUM_TRANSFER_AMOUNT) {
            emit TransferRestricted(from, to, amount);
            revert TransferAmountTooLow();
        }
        return super.transferFrom(from, to, amount);
    }
}

